<?phpfunction kent_process_css($css, $theme) {    // Set the menu hover color    if (!empty($theme->settings->menuhovercolor)) {        $menuhovercolor = $theme->settings->menuhovercolor;    } else {        $menuhovercolor = null;    }    $css = kent_set_menuhovercolor($css, $menuhovercolor);    	// Set the background image for the graphic wrap     if (!empty($theme->settings->graphicwrap)) {        $graphicwrap = $theme->settings->graphicwrap;    } else {        $graphicwrap = null;    }    $css = kent_set_graphicwrap($css, $graphicwrap);        return $css;}function kent_set_menuhovercolor($css, $menuhovercolor) {    $tag = '[[setting:menuhovercolor]]';    $replacement = $menuhovercolor;    if (is_null($replacement)) {        $replacement = '#5faff2';    }    $css = str_replace($tag, $replacement, $css);    return $css;}function kent_set_graphicwrap($css, $graphicwrap) {	global $OUTPUT;  	$tag = '[[setting:graphicwrap]]';	$replacement = $graphicwrap;	if (is_null($replacement)) { 		$replacement = $OUTPUT->pix_url('graphics/fish', 'theme'); 	}	$css = str_replace($tag, $replacement, $css);	return $css;}/* * Function to return google analytics with code, only if the code is set via the config */function kent_set_analytics(){    global $CFG;    $ga_code = "";	$ga_code_number = "";		if (isset($CFG->google_analytics_code) && $CFG->google_analytics_code != ""){              $ga_code_number = $CFG->google_analytics_code;	}	if ($ga_code_number != ""){	$ga_code = <<<GACODE<!-- start of google analytics --><script type="text/javascript">  var _gaq = _gaq || [];  _gaq.push(['_setAccount', '{$ga_code_number}']);  _gaq.push(['_trackPageview']);  (function() {    var ga = document.createElement('script'); ga.type = 'text/javascript'; ga.async = true;    ga.src = ('https:' == document.location.protocol ? 'https://ssl' : 'http://www') + '.google-analytics.com/ga.js';    var s = document.getElementsByTagName('script')[0]; s.parentNode.insertBefore(ga, s);  })();</script><!-- end of google analytics -->GACODE;    }    return $ga_code;}